import React, { Component } from "react";
import Header from "../components/Header";
import { auth } from "../services/firebase";
import { db } from "../services/firebase";

export default class chat1 extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: auth().currentUser,
      chats: [],
      content: '',
      content1: '',
      content2: '',
      content3: '',
      content4: '',
      readError: null,
      writeError: null,
      loadingChats: false
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleChange1 = this.handleChange1.bind(this);
    this.handleChange2 = this.handleChange2.bind(this);
    this.handleChange3 = this.handleChange3.bind(this);
    this.handleChange4 = this.handleChange4.bind(this);

    this.handleSubmit = this.handleSubmit.bind(this);
    this.myRef = React.createRef();
  }

  async componentDidMount() {
    this.setState({ readError: null, loadingChats: true });
    const chatArea = this.myRef.current;
    try {
      db.ref("chats").on("value", snapshot => {
        let chats = [];
        snapshot.forEach((snap) => {
          chats.push(snap.val());
        });
        chats.sort(function (a, b) { return a.timestamp - b.timestamp })
        this.setState({ chats });
        chatArea.scrollBy(0, chatArea.scrollHeight);
        this.setState({ loadingChats: false });
      });
    } catch (error) {
      this.setState({ readError: error.message, loadingChats: false });
    }
  }

  handleChange(event) {
    this.setState({
      content: event.target.value,
    });
  }

  handleChange1(event) {
    this.setState({
      content1: event.target.value,
    });
  }
  handleChange2(event) {
    this.setState({
      content2: event.target.value,
    });
  }
  handleChange3(event) {
    this.setState({
      content3: event.target.value,
    });
  }
  handleChange4(event) {
    this.setState({
      content4: event.target.value,
    });
  }
  async handleSubmit(event) {
    event.preventDefault();
    this.setState({ writeError: null });
    const chatArea = this.myRef.current;
    try {
      await db.ref("chats").push({
        content: this.state.content,
        content1: this.state.content1,
        content2: this.state.content2,
        content3: this.state.content3,
        content4: this.state.content4,
        timestamp: Date.now(),
        uid: this.state.user.uid
      });
      this.setState({ content: '',content1: '',content2: '',content3: '',content4: '', });
      chatArea.scrollBy(0, chatArea.scrollHeight);
    } catch (error) {
      this.setState({ writeError: error.message });
    }
  }
  async handleSubmit(event) {
    event.preventDefault();
    this.setState({ writeError: null });
    const chatArea = this.myRef.current;
    try {


      chatArea.scrollBy(0, chatArea.scrollHeight);
    } catch (error) {
      this.setState({ writeError: error.message });
    }
  }
  formatTime(timestamp) {
    const d = new Date(timestamp);
    const time = `${d.getDate()}/${(d.getMonth()+1)}/${d.getFullYear()} ${d.getHours()}:${d.getMinutes()}`;
    return time;
  }

  render() {
    return (
      <div>

<div class="card m-5">
  <div class="card-header">
    Featured
  </div>
  <div class="card-body">
    <h5 class="card-title">Special title treatment</h5>


    <Header />

<div className="chat-area" ref={this.myRef}>
  {/* loading indicator */}
  {this.state.loadingChats ? <div className="spinner-border text-success" role="status">
    <span className="sr-only">Loading...</span>
  </div> : ""}
  {/* chat area */}
  {this.state.chats.map(chat => {
    return<form onSubmit={this.handleSubmit1} className="mx-3">
    <p key={chat.timestamp} className={"chat-bubble " + (this.state.user.uid === chat.uid ? "current-user" : "")}>
      {chat.content}<br />
      {chat.content1}<br />
      {chat.content2}<br />
      {chat.content3}<br />
      {chat.content4}<br />
      <br />
      <button type="submit" className="btn btn-submit px-5 mt-4">Remove</button>
      <span className="chat-time float-right">{this.formatTime(chat.timestamp)}</span>
    </p>
    </form> 
  })}
</div>
<form onSubmit={this.handleSubmit} className="mx-3">
  <label >First Name:</label>
  <input type="text" className="form-control" name="content" onChange={this.handleChange} value={this.state.content}></input>
  <label >Email:</label>
  <input type="email" className="form-control" name="content1" onChange={this.handleChange1} value={this.state.content1}></input>
  <label >Password:</label>
  <input type="password" className="form-control" name="content2" onChange={this.handleChange2} value={this.state.content2}></input>
  <label >Service:</label>
  <input type="text" className="form-control" name="content3" onChange={this.handleChange3} value={this.state.content3}></input>
  <label >Numero Telephone:</label>
  <input type="text" className="form-control" name="content4" onChange={this.handleChange4} value={this.state.content4}></input>
  {this.state.error ? <p className="text-danger">{this.state.error}</p> : null}
  <button type="submit" className="btn btn-submit px-5 mt-4">Send</button>
</form>
<div className="py-5 mx-3">
  Login in as: <strong className="text-info">{this.state.user.email}</strong>
</div>


</div>
    <a href="#" class="btn btn-primary">Go somewhere</a>
  </div>


</div>



    );
  }
}
