import React, { Component } from "react";
import Header from "../components/Header";
import { auth } from "../services/firebase";
import { db } from "../services/firebase";

export default class chat1 extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: auth().currentUser,
      chats: [],
      content: '',
      content1: '',
      content2: '',
      content3: '',
      content4: '',
      readError: null,
      writeError: null,
      loadingChats: false
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleChange1 = this.handleChange1.bind(this);
    this.handleChange2 = this.handleChange2.bind(this);
    this.handleChange3 = this.handleChange3.bind(this);
    this.handleChange4 = this.handleChange4.bind(this);

    this.handleSubmit = this.handleSubmit.bind(this);
    this.myRef = React.createRef();
  }

  async componentDidMount() {
    this.setState({ readError: null, loadingChats: true });
    const chatArea = this.myRef.current;
    try {
      db.ref("chats").on("value", snapshot => {
        let chats = [];
        snapshot.forEach((snap) => {
          chats.push(snap.val());
        });
        chats.sort(function (a, b) { return a.timestamp - b.timestamp })
        this.setState({ chats });
        chatArea.scrollBy(0, chatArea.scrollHeight);
        this.setState({ loadingChats: false });
      });
    } catch (error) {
      this.setState({ readError: error.message, loadingChats: false });
    }
  }

  handleChange(event) {
    this.setState({
      content: event.target.value,
    });
  }

  handleChange1(event) {
    this.setState({
      content1: event.target.value,
    });
  }
  handleChange2(event) {
    this.setState({
      content2: event.target.value,
    });
  }
  handleChange3(event) {
    this.setState({
      content3: event.target.value,
    });
  }
  handleChange4(event) {
    this.setState({
      content4: event.target.value,
    });
  }
  async handleSubmit(event) {
    event.preventDefault();
    this.setState({ writeError: null });
    const chatArea = this.myRef.current;
    try {
      await db.ref("chats").push({
        content: this.state.content,
        content1: this.state.content1,
        content2: this.state.content2,
        content3: this.state.content3,
        content4: this.state.content4,
        timestamp: Date.now(),
        uid: this.state.user.uid
      });
      this.setState({ content: '',content1: '',content2: '',content3: '',content4: '', });
      chatArea.scrollBy(0, chatArea.scrollHeight);
    } catch (error) {
      this.setState({ writeError: error.message });
    }
  }
  async handleSubmit1(event) {
    event.preventDefault();
    this.setState({ writeError: null });
    const chatArea = this.myRef.current;
    try {
      await db.ref("chats").child(this.state.id).remove();

      chatArea.scrollBy(0, chatArea.scrollHeight);
    } catch (error) {
      this.setState({ writeError: error.message });
    }
  }
  formatTime(timestamp) {
    const d = new Date(timestamp);
    const time = `${d.getDate()}/${(d.getMonth()+1)}/${d.getFullYear()} ${d.getHours()}:${d.getMinutes()}`;
    return time;
  }

  render() {
    return (
      <div>
<hr />
<br />
<br />
<div class="card m-5">
  <div class="card-header">
  Add new Fournisseur:
  </div>
  <div class="card-body">
    <Header />
<form onSubmit={this.handleSubmit} className="mx-3">
<div class="form-group">
    <label for="inputFullName">Full Name</label>
    <input type="text" class="form-control" id="inputFullName" placeholder="Full Name" name="content" onChange={this.handleChange} value={this.state.content}/>
  </div>
  <div class="form-row">
    <div class="form-group col-md-6">
      <label for="inputEmail4">Email</label>
      <input type="email" class="form-control" id="inputEmail4" placeholder="Email" name="content1" onChange={this.handleChange1} value={this.state.content1}/>
    </div>
    <div class="form-group col-md-6">
      <label for="inputPassword4">Password</label>
      <input type="password" class="form-control" id="inputPassword4" placeholder="Password" name="content2" onChange={this.handleChange2} value={this.state.content2}/>
    </div>
  </div>
  <div class="form-group">
    <label for="inputAddress">Address</label>
    <input type="text" class="form-control" id="inputAddress" placeholder="Wilaya/Dayra/Baladia" name="content3" onChange={this.handleChange3} value={this.state.content3}/>
  </div>

  <div class="form-row">
    <div class="form-group col-md-6">
      <label for="inputPhoneNumber">Phone Number</label>
      <input type="text" class="form-control" id="inputPhoneNumber" name="content4" onChange={this.handleChange4} value={this.state.content4}/>
    </div>
    <div class="form-group col-md-4">
      <label for="inputServiceType">Service Type</label>
      <select id="inputServiceType" class="form-control">
        <option selected>Choose...</option>
        <option>Ambulancier</option>
        <option>Photographe</option>
        <option>Soudeur</option>
        <option>Pharmacien</option>
        <option>Professeur de langues</option>
      </select>
    </div>
    <div class="form-group col-md-2">
      <label for="inputDescription">Description</label>
      <text type="text" class="form-control" id="inputDescription" />
    </div>
  </div>
  <button type="submit" class="btn btn-primary">Sign in</button>
</form>

<div className="py-5 mx-3">
  Login in as: <strong className="text-info">{this.state.user.email}</strong>
</div>

</div>
</div>
</div>


    );
  }
}
