import React, { Component } from "react";
import {
  Route,
  BrowserRouter as Router,
  Switch,
  Redirect
} from "react-router-dom";
import Home from "./pages/Home";
import Chat from "./pages/Chat";
import Chat1 from "./pages/Chat1";
import Signup from "./pages/Signup";
import Login from "./pages/Login";
import AddProvider from "./pages/UpdateProvider";
import ManageProviders from "./pages/ManageProviders";
import { auth } from "./pages/services/firebase";
import './styles.css';
import ManageProvider from "./pages/ManageProviders";

function PrivateRoute({ component: Component, authenticated, ...rest }) {
  return (
    <Route
      {...rest}
      render={props =>
        authenticated === true ? (
          <Component {...props} />
        ) : (
            <Redirect
              to={{ pathname: "/login", state: { from: props.location } }}
            />
          )
      }
    />
  );
}

function PublicRoute({ component: Component, authenticated, ...rest }) {
  return (
    <Route
      {...rest}
      render={props =>
        authenticated === false ? (
          <Component {...props} />
        ) : (
            <Redirect to="/chat" />
          )
      }
    />
  );
}

class App extends Component {
  constructor() {
    super();
    this.state = {
      authenticated: false,
      loading: true
    };
  }

  componentDidMount() {
    auth().onAuthStateChanged(user => {
      if (user) {
        this.setState({
          authenticated: true,
          loading: false
        });
      } else {
        this.setState({
          authenticated: false,
          loading: false
        });
      }
    });
  }

  render() {
    return this.state.loading === true ? (
      <div className="spinner-border text-success" role="status">
        <span className="sr-only">Loading...</span>
      </div>
    ) : (
        <Router>
          <Switch>
            <Route exact path="/" component={Home} />
            <PrivateRoute
              path="/chat"
              authenticated={this.state.authenticated}
              component={Chat}
            />
            <Route exact path="/" component={Home} />
            <PrivateRoute
              path="/manageProviders"
              authenticated={this.state.authenticated}
              component={ManageProviders}
            />
            <PrivateRoute
              path="/addProvider"
              authenticated={this.state.authenticated}
              component={AddProvider}
            />
            <PrivateRoute
              path="/updateProvider"
              authenticated={this.state.authenticated}
              component={AddProvider}
            />
            <PrivateRoute
              path="/chat1"
              authenticated={this.state.authenticated}
              component={Chat1}
            />
            <PublicRoute
              path="/signup"
              authenticated={this.state.authenticated}
              component={Signup}
            />
            <PublicRoute
              path="/login"
              authenticated={this.state.authenticated}
              component={Login}
            />
          </Switch>
        </Router>
      );
  }
}

export default App;
