import React, { Component } from "react";
import Header from "../../components/Header";
import { auth } from "../services/firebase";
import { db } from "../services/firebase";

export default class addService extends Component {

      constructor(props) {
        super(props);
        this.state = {
          user: auth().currentUser,
          chats: [],
          content: '',
          content1: '',
          content2: '',
          content3: '',
          content4: '',
          readError: null,
          writeError: null,
          loadingChats: false
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleChange1 = this.handleChange1.bind(this);
        this.handleChange2 = this.handleChange2.bind(this);
        this.handleChange3 = this.handleChange3.bind(this);
        this.handleChange4 = this.handleChange4.bind(this);

        this.handleSubmit = this.handleSubmit.bind(this);
        this.myRef = React.createRef();
      }

      async componentDidMount() {
        this.setState({ readError: null, loadingChats: true });
        const chatArea = this.myRef.current;
        try {
          db.ref("chats").on("value", snapshot => {
            let chats = [];
            snapshot.forEach((snap) => {
              chats.push(snap.val());
              console.log(snap.val())
            });
            chats.sort(function (a, b) { 
             return a.timestamp - b.timestamp })
            this.setState({ chats });
            this.setState({ loadingChats: false });
          });
        } catch (error) {
          this.setState({ readError: error.message, loadingChats: false });
        }
      }

      handleChange(event) {
        this.setState({
          content: event.target.value,
        });
      }

      handleChange1(event) {
        this.setState({
          content1: event.target.value,
        });
      }
      handleChange2(event) {
        this.setState({
          content2: event.target.value,
        });
      }
      handleChange3(event) {
        this.setState({
          content3: event.target.value,
        });
      }
      handleChange4(event) {
        this.setState({
          content4: event.target.value,
        });
      }
      async handleSubmit(event) {
        event.preventDefault();
        this.setState({ writeError: null });
        const chatArea = this.myRef;
        console.log(chatArea);
        let myMap = new Map();
        myMap.set("name", "value associated with 'a string'")
        myMap.set("email", "value associated with keyObj")
        myMap.set("phone", "value associated with keyFunz")
        myMap.set("ServiceType", "value associated with keyFunc")
        myMap.set("ServiceDescription", "value associated with keyFunc")
        try {
          await db.ref("chatsxcwc").child(this.state.user.uid).push("dsfsfsdfs");
          this.setState({ content: '',content1: '',content2: '',content3: '',content4: '', });
          chatArea.scrollBy(0, chatArea.scrollHeight);
        } catch (error) {
          this.setState({ writeError: error.message });
        }
      }
      async handleSubmit1(event) {
        event.preventDefault();
        this.setState({ writeError: null });
        const chatArea = this.myRef.current;
        try {
          await db.ref("chats").child(this.state.id).remove();

          chatArea.scrollBy(0, chatArea.scrollHeight);
        } catch (error) {
          this.setState({ writeError: error.message });
        }
      }
      formatTime(timestamp) {
        const d = new Date(timestamp);
        const time = `${d.getDate()}/${(d.getMonth()+1)}/${d.getFullYear()} ${d.getHours()}:${d.getMinutes()}`;
        return time;
      }

      render() {
        return (
          <div>
   <Header />
   <br/>
   <br/>
   <br/>sdfsd
   <br/>sdfsf
   <br/>sdfsdf
   <br/>sdfsdf
   <br/>sdfsdf
   <br/>fsdfds
   <br/>


    <form onSubmit={this.handleSubmit} className="mx-3">
    <div class="form-group">
        <label for="inputFullName">Enter Service Type : </label>
        <input type="text" class="form-control" id="inputFullName" placeholder="Full Name" name="content" onChange={this.handleChange} value={this.state.content}/>
      </div>

      {this.state.error ? <p className="text-danger">{this.state.error}</p> : null}
      <button type="submit" class="btn btn-primary">Add</button>
    </form>


    </div>
    );
  }
}
